import React from 'react';

function CharacterListItem(props){
    const array=Array.from(props.character);
    const listItems=array.map((item) =>  
        <img src={item.thumbnail.path+'/portrait_medium.'+item.thumbnail.extension} alt={item.name}></img>
    );
    return(
        <div>
            <div className="list-item">
      <div className="row">
        <div className="col-md-4">
          <img
            src={listItems}
          />
        </div>
        <div className="col-md-8">
          <div className="row">
            <div className="col-md-12">
              <h3>{character.name}</h3>
              {character.description && <hr />}
              {character.description && character.description.length > 50
                ? character.description.substr(0, 50) + '...'
                : character.description}
            </div>
          </div>
        </div>
      </div>
    </div>
        </div>
    );
}

export default CharacterListItem;
